import styled from 'styled-components';

export const ProductList = styled.ul`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 20px;
  list-style: none;
  width:1000px;
  margin: 0 auto;

  li {
    display: flex;
    flex-direction: column;
    background: #fff;
    border-radius: 4px;
    padding: 20px;
    li {
      vertical-align: middle;
      height: 420px;
      flex-direction: row;
      align-items: center;
    }
    img {
      align-self: center;
      max-width: 245px;
    }

    > strong {
      font-size: 1rem;
      line-height: 20px;
      color: #333;
      margin-top: 5px;
      text-align: center;
    }

    > span {
      font-size: 21px;
      font-weight: bold;
      margin: 5px 0 20px;
    }
  }
`;
export const Total = styled.div`
  text-align: right;
  margin-left:auto;
  margin-right0:0;
`;
